mapper.select:
select *
from offer_report
where order_id = #{oid}
  and provider_id = #{pid};

mapper.update:
update `order`
set provider_id = #{providerId},
    quality     = '寰堝ソ',
    price       = #{price},
    end_date    = #{supplyDate}
where id = #{orderId}

update offer_report
set provider_status = 4
where provider_id = #{pid}
  and order_id = #{oid}

mapper.select:
select * from `order` where year(end_date) = year(now()) and order_status = #{orderStatus}
and department_id in (1,2,3,4)
and department_id in (5,6)
and department_id = #{departmentId}

mapper.update:
update provider
set provider_status = 0
where id = #{pid}
